import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CreateContract.css';

function CreateContract() {
  const [contractTitle, setContractTitle] = useState('');
  const [contractDetails, setContractDetails] = useState('');
  const [connections, setConnections] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchConnections = async () => {
      const token = localStorage.getItem('token');

      if (!token) {
        alert('You are not authenticated. Please log in.');
        return;
      }

      try {
        const response = await fetch('http://localhost:8000/api/v1/connections', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (!response.ok) {
          const errorResponse = await response.json();
          throw new Error('Failed to fetch connections: ' + errorResponse.message);
        }

        const result = await response.json();
        setConnections(result.connections);
      } catch (error) {
        alert(error.message);
      }
    };

    fetchConnections();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulate contract creation logic
    console.log('Contract Created:', { contractTitle, contractDetails });
    alert('Contract Created Successfully!');
    // Reset form fields
    setContractTitle('');
    setContractDetails('');
  };

  return (
    <div className="contract">
      <h2>Make a Contract</h2>
      <h3>Your Connections</h3>
      {connections.length > 0 ? (
        <ul>
          {connections.map((connection) => (
            <li key={connection.connection_id}>
              {connection.friend_name} - {connection.friend_email} - {connection.friend_phone}
            </li>
          ))}
        </ul>
      ) : (
        <p>No connections found.</p>
      )}
      <form onSubmit={handleSubmit}>
        <label htmlFor="contractTitle">Contract Title:</label>
        <input
          type="text"
          id="contractTitle"
          name="contractTitle"
          value={contractTitle}
          onChange={(e) => setContractTitle(e.target.value)}
          required
        />

        <label htmlFor="contractDetails">Contract Details:</label>
        <textarea
          id="contractDetails"
          name="contractDetails"
          value={contractDetails}
          onChange={(e) => setContractDetails(e.target.value)}
          required
        ></textarea>

        <button type="submit">Submit Contract</button>
      </form>

      <h2>Review Previous Contracts</h2>
      <ul>
        <li>Contract 1</li>
        <li>Contract 2</li>
        <li>Contract 3</li>
      </ul>
    </div>
  );
}

export default CreateContract;
