import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './PendingContracts.css';

function PendingContracts() {
  const [pendingContracts, setPendingContracts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPendingContracts = async () => {
      const token = localStorage.getItem('token');

      if (!token) {
        alert('You are not authenticated. Please log in.');
        return;
      }

      try {
        const response = await fetch('http://localhost:8000/api/v1/pending-offers', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (!response.ok) {
          const errorResponse = await response.json();
          throw new Error('Failed to fetch pending contracts: ' + errorResponse.message);
        }

        const result = await response.json();
        setPendingContracts(result.contracts || []);
      } catch (error) {
        alert(error.message);
      }
    };

    fetchPendingContracts();
  }, []);

  // Filter pending contracts based on search term
  const filteredContracts = pendingContracts.filter(contract =>
    contract.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    contract.details.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleAcceptOffer = async (contractId) => {
    // Logic to accept the offer
    console.log(`Accepting offer for contract ID: ${contractId}`);
    // Call the API to accept the offer
  };

  const handleCounterOffer = async (contractId) => {
    // Logic to counter the offer
    console.log(`Countering offer for contract ID: ${contractId}`);
    // Call the API to counter the offer
  };

  const handleRejectOffer = async (contractId) => {
    // Logic to reject the offer
    console.log(`Rejecting offer for contract ID: ${contractId}`);
    // Call the API to reject the offer
  };

  return (
    <div className="pending-contracts">
      <h2>Pending Contracts</h2>
      <input
        type="text"
        placeholder="Search contracts..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ marginBottom: '10px', width: '100%' }}
      />
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Contract Title</th>
            <th>Details</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredContracts.length > 0 ? (
            filteredContracts.map((contract) => (
              <tr key={contract.contract_id}>
                <td>{contract.title}</td>
                <td>{contract.details}</td>
                <td>{contract.status}</td>
                <td>
                  <button onClick={() => handleAcceptOffer(contract.contract_id)}>Accept Offer</button>
                  <button onClick={() => handleCounterOffer(contract.contract_id)}>Counter Offer</button>
                  <button onClick={() => handleRejectOffer(contract.contract_id)}>Reject Offer</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No pending contracts found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default PendingContracts;
